```
/*
	O(n^2) + O(plogp) where p is number of palindromes

	Algorithm: for each posible palindrome starting charactera look at each possible palindrome end character, and if valid, add it's length and mask of bits, set corresponding to which positions were used by the palindrome, to a list of palindromes 

	Then sort the list from longest to shortest palindrome and check to see which two, lengths multiplied together, are the maximum value.

*/
var maxProduct = function(s) {
    let seen = [];
    function dfs(lo, hi, len, used) {
        for (let i = lo; i < hi; ++i) {
            for (let j = hi; j >= i; --j) {
                if (s[i] === s[j]) {
                    if (i === j) {
                        if (len > 0) {
                            seen.push([len + 1, used | (1 << i)]);
                        }
                    } else if (i === j - 1) {
                        seen.push([len + 2, used | (1 << i) | (1 << j)]);
                    } else if (i === j - 2) {
                        seen.push([len + 3, used | (1 << i) | (1 << j) | (1 << (i + 1))]);       
                        seen.push([len + 2, used | (1 << i) | (1 << j)]);  
                    } else {
                        seen.push([len + 2, used | (1 << i) | (1 << j)]);                          
                        dfs(i + 1, j - 1, len + 2, used | (1 << i) | (1 << j));
                    }
                } else if (len > 0) {
                    seen.push([len + 1, used | (1 << j)]);
                }
            }
        }
    }
    for (let i = 0; i < s.length; ++i) {
        seen.push([1, 1 << i]);
    }
    dfs(0, s.length - 1, 0, 0);
    seen.sort( (a,b) => b[0] - a[0] );
    let ans = 1;
    for (let i = 0; i < seen.length; ++i) {
        for (let j = i + 1; j < seen.length; ++j) {
            if ((seen[i][1] & seen[j][1]) === 0) {
                let num = seen[i][0] * seen[j][0];
                if (num < ans) {
                    break;
                }
                ans = num;
            }
        }
    }
    return ans;
};

