class Solution:
    def maxProduct(self, s: str) -> int:

        @lru_cache(None)
        def findBigPalLen(x): #O(n^2)
            if len(x) <= 1: return len(x)
            if x[0] == x[-1]: return 2 + findBigPalLen(x[1:-1])
            return max(findBigPalLen(x[1:]), findBigPalLen(x[:-1]))

        @lru_cache(None)
        def findAllPal(x): #Fast pruning
            if len(x) == 0: 
                return [""], [x]

            Idx = defaultdict(list)
            for i,l in enumerate(x): 
                Idx[l].append(i)

            X, Y = [""], [x]
            for i in range(len(x)):
                X.append(x[i])
                Y.append(x[:i] + x[i + 1:])
                for j in Idx[x[i]]:
                    if j > i:
                        X1, Y1 = findAllPal(x[i + 1 : j])
                        for k in range(len(X1)):
                            X.append(x[i] + X1[k] + x[j])
                            Y.append(x[:i] + Y1[k] + x[j + 1:])
            return X,Y
        X, Y = findAllPal(s)
        return max(len(X[i]) * findBigPalLen(Y[i]) for i in range(len(X)))
