class Solution {
public:
    int res, n;
    string str;
    bool isPalin(string s, int n) {
        for (int i=0; i<n/2; i++) {
            if (s[i] != s[n-1-i]) return false;
        }
        return true;
    }
    int max(int a, int b) {
        if (a >= b) {
            return a;
        }
        return b;
    }
    int max(int a, int b, int c) {
        int m = max(a,b);
        return max(m,c);
    }
    int go(int idx, string &s1, string &s2) {
        if (idx == n) {
            int l1 = s1.size();
            int l2 = s2.size();
            if (isPalin(s1, l1) && isPalin(s2, l2)) {
                res = max(res, l1*l2);
            }
            return res;
        }
        string s3 = s1 + str[idx];
        string s4 = s2 + str[idx];
        return max(go(idx+1, s1, s2), go(idx+1, s3, s2), go(idx+1, s1, s4));
    }
    int maxProduct(string s) {
        n = s.size();
        res = 0;
        str = s;
        string s1 = "";
        string s2 = "";
        return max(go(0,s1,s2), res);
    }
};
