Check All subsequences
and check their lps.
then finally return the max possible ans.
```
class Solution {
public:
    int ans=1;
    unordered_map<string,int>um;
    void func(string s,string x,string y,int i){

        if(i==s.length()){
            ans=max(ans,functiontofindlps(x)*functiontofindlps(y));
            return ;
        }
        func(s,x+s[i],y,i+1);
        func(s,x,y+s[i],i+1);
    }
    int functiontofindlps(string s){
        int n=s.length();
        if(n==0)return 0;
        if(um.count(s))return um[s];
        vector<vector<int>>dp(n+1,vector<int>(n+1));
        string r=s;
        reverse(s.begin(),s.end());
        for(int i=1;i<=n;i++){
            for(int j=1;j<=n;j++){
                if(s[i-1]==r[j-1]){
                    dp[i][j]=dp[i-1][j-1]+1;
                }else{
                    dp[i][j]=max(dp[i-1][j],dp[i][j-1]);
                }
            }
        }
        return um[s]=dp[n][n];
    }
    int maxProduct(string s) {
        func(s,"","",0);
        return ans;
    }
};
