    bool overlap(vector<int> &a, vector<int> &b){

        int i = 0, j = 0;
        int n = a.size(), m = b.size();

        while(i<n and j<m)
        {
            if(a[i]==b[j])return 0;
            else if(a[i]<b[j])i++;
            else j++;
        }
        return 1;
