    int maxProduct(string s) {

        int n = s.size();

        unordered_set<char> st;
        for(char curr:s)st.insert(curr);

        // If the string contains only single character
        if(st.size()==1)
        {
            return (n/2)*((n+1)/2);
        }


        // Generate all the subsequences and store the indices of those
        // Indices will help us to find which subsequences overlap
        vector<vector<int>> allsub;

        // Generate all using bitwise
        for(int i = 0; i<pow(2,n); i++)
        {
            vector<int> temp;
            for(int j = 0; j<n; j++){
                if(i&(1<<j))temp.push_back(j);
