solutions.content:
1. First generate all subsequences of the string using take, not take approach and backtracking.
2. Then check if each subsequence is palindrome.
3. If palindrome then find bitmask of that palindrome subsequence and store the length of that palindrome in hashmap.
4. Then iterate through every pair of the hashmap item and if no common index between them then store the max product.

```
class Solution {
private:
    unordered_map<int,int> palLength;
private:
    int n = 0;
public:
    int maxProduct(string s) {
        this->n = s.length();
        vector<int> sub;
        recursion(s, 0, sub);

        int result = 1;
        for(auto pal1 : palLength) {
            for(auto pal2 : palLength) {
                if((pal1.first & pal2.first) == 0) {
                    int current = pal1.second * pal2.second;
                    result = max(result, current);
                }
            }
        }

        return result;
    }

private:
    void recursion(string s, int index, vector<int>& sub) {
        if(index == n) {
            if(isPalindrome(s, sub)) {
                int mask = getMask(sub);
                palLength[mask] = sub.size();
            }

            return;
        }

        // Not take the current
        recursion(s, index + 1, sub);

        // Take the current
        sub.push_back(index);
        recursion(s, index + 1, sub);

        // Backtrack
        sub.pop_back();
    }

private:
    bool isPalindrome(string& s, vector<int>& sub) {
        int i = 0;
        int j = sub.size() - 1;

        while(i < j) {
            if(s[sub[i]] != s[sub[j]]) {
                return false;
            }

            i++;
            j--;
        }

        return true;
    }

    int getMask(vector<int>& sub) {
        int mask = 0;
        for(int index : sub) {
            mask = mask | (1 << index);
        }

        return mask;
    }
};

// Time Complexity: (4^N)
// Space Complexity: O(4^N) + call stack
