class Solution {
    func maxProduct(_ s: String) -> Int {

        let s = Array(s)

        func pals(_ l: Int = 0, _ r: Int = s.count - 1) -> [[Int]] {
            guard l <= r else { return [[]] }
            var res: [[Int]] = [[]] + (l...r).map { [$0] }
            var b = l
            while b < r {
                var e = r
                while b < e {
                    while b < e, s[b] != s[e] { e -= 1 }
                    if b < e { res += pals(b + 1, e - 1).map { [b] + $0 + [e] } }
                    e -= 1
                }
                b += 1
            }
            return res
        }

        var res = 0
        let vars = pals()
            .map(Set.init)
            .sorted { $0.count > $1.count }

        for i in 0..<vars.count - 1 {
            if vars[i].count * vars[i].count < res { break }
            for j in 1 + i..<vars.count {
                if vars[i].count * vars[j].count < res { break }
                if vars[i].intersection(vars[j]).isEmpty {
                    res = max(res, vars[i].count * vars[j].count)
                }
            }
        }

        return res
    }
}
