class Solution {
public:
    // To check whether a string is palindrome or not.
    bool isPalindrome(string s) {
        int n = s.size();
        for (int i = 0; i < n / 2; i++) {
            if (s[i] != s[n-i-1]) {
                return false;
            }
        }
        return true;
    }
    int maxProduct(int i, string s1, string s2, string &s) {
        // If we have traversed the whole string, we check the two strings picked on the way whether they are
        // palindrome or not. If they are, we can return their product.
        if (i == s.size()) {

            if (isPalindrome(s1) && isPalindrome(s2)) {
                return (s1.size()) * (s2.size());
            }
            return 0;
        }

        // We have two options for every index either not pick it or pick it.
        // If we want to pick it we can add it to string1 or string2 but not both simultaneously.
        int notTake = maxProduct(i + 1, s1, s2, s); 
        int take1 = maxProduct(i + 1, s1 + s[i], s2, s);
        int take2 = maxProduct(i + 1, s1, s2 + s[i], s);

        // Finally we take max of all options.
        return max({notTake, take1, take2});
    }
    int maxProduct(string s) {
        return maxProduct(0, "", "", s); // [index, string1, string2, string]
    }
};
```

