class Solution {
  private boolean isPalindrome(String s) {
    int i = 0, j = s.length() - 1;
    while (i <= j && s.charAt(i) == s.charAt(j)) {
      i++;
      j--;
    }

    return j < i;
  }

  private void generatePalSubseq(
    String s, String subseq, int curIndex, int mask, Map < Integer, List < Integer >> maskCache
  ) {
    if (curIndex == s.length()) {
      return;
    }

    String currS = subseq + s.substring(curIndex, curIndex + 1);
    Integer currM = mask | (1 << curIndex);
    List < Integer > ml = null;

    if (currS.length() == 1) {
      if (maskCache.containsKey(1)) {
        maskCache.get(1).add(currM);
      } else {
        ml = new ArrayList < > ();
        ml.add(currM);
        maskCache.put(1, ml);
      }
    }

    if (currS.length() > 1 &&
      currS.charAt(0) == currS.charAt(currS.length() - 1) &&
      this.isPalindrome(currS)
    ) {
      ml = maskCache.get(currS.length());
      if (ml != null) {
        ml.add(currM);
      } else {
        ml = new ArrayList < Integer > ();
        ml.add(currM);
        maskCache.put(currS.length(), ml);
      }
    }

    // include current Index
    this.generatePalSubseq(
      s,
      currS,
      curIndex + 1,
      currM,
      maskCache
    );

    // exclude current Index
    this.generatePalSubseq(s, subseq, curIndex + 1, mask, maskCache);
  }

  public int maxProduct(String s) {
    if (s.length() == 2) {
      return 1;
    }

    Map < Integer, List < Integer >> palMasks = new HashMap < > ();
    this.generatePalSubseq(s, "", 0, 0, palMasks);
    int maxP = 1;

    for (int i = s.length() - 1; i >= 1; i--) {
      if (palMasks.containsKey(i)) {
        int lim = maxP / i;
        if (lim > i) {
          continue;
        }

        List < Integer > cndList = null;
        List < Integer > maskleni = palMasks.get(i);
        for (Integer l: maskleni) {
          int m = i;
          while (m <= i && m >= lim) {
            if (palMasks.containsKey(m)) {
              cndList = palMasks.get(m);
              for (Integer c: cndList) {
                if (c != l && (c & l) == 0) {
                  maxP = Math.max(maxP, i * m);
                  break;
                }
              }
            }
            m--;
          }
        }
      }
    }

    return maxP;
  }
}
```

