class Solution {
public:
    bool palindrome(string s)
    {
        int i=0, j=s.size()-1;
        while(i<j)
        {
            if(s[i]!=s[j])
                return false;
            i++;
            j--;
        }
        return true;
    }

    int maxProduct(string s) {
        int N=s.size();
        map<int, int> pals;

        for(int mask=1; mask < (1<<N); mask++)
        {
            string subseq="";
            for(int i=0;i<N;i++)
            {
                if(mask & (1<<i))
                    subseq += s[i];
            }
            if(palindrome(subseq))
                pals[mask] = subseq.length();
        }

        int ans=INT_MIN;
        for(auto k:pals)
        {
            for(auto j:pals)
            {
                if((k.first&j.first)==0)
                    ans = max(ans, k.second*j.second);
            }
        }

        return ans;
    }
};
