class Solution {
public:
    int maxProduct(string s) {
        // We considering subsequences as bitwise masks, so since 0 length
        // subsequence will never be palindrome, we start from 1 & go till 
        // 2^N -1(which will denote all elements to be included- all 1s)
        // to check what substring corresponds to a bitmask, we try to 
        // AND the bitmask, with masks having one at each index of string.
        // corresponding to 1 << i, so at all places we have char in bitmask,
        // the ANd will be 1, and we can deduce the current subsequence,
        // check whether it's a palindrome- compare it with reverse
        // if yes, add in a map, bitmask vs length,
        // so for pairs of bitmask in this map whose AND is 1, 
        // we can multiply the lengths & track the max 

        unordered_map<int, int> mp;
        int n = s.length();
        for(int mask = 1; mask < (1 << n); mask++){
            string subSeq = "";
            for(int i = 0; i < n; i++){
                if(mask & (1<< i)){
                    subSeq += s[i];
                }
            }
            string revSub = subSeq;
            reverse(revSub.begin(), revSub.end());
            if(subSeq == revSub){
                mp[mask] = subSeq.length();
            }
        }
        int maxProd = 1;

        cout << endl;
        for(auto it: mp){
            for(auto it2: mp){
                if((it.first & it2.first) == 0){
                    maxProd = max(maxProd, it.second*it2.second);
                }
            }
        }

        return maxProd;
    }
};
