func maxProduct(s string) int {
	var IsPelindrom func(s string, pKnowledge map[string]bool) bool
	IsPelindrom = func(s string, pKnowledge map[string]bool) bool {
		if v, ok := pKnowledge[s]; ok {
			return v
		}
		i, j := 0, len(s)-1
		for i < j {
			if s[i] != s[j] {
				pKnowledge[s] = false
				return false
			}
			i++
			j--
		}

		pKnowledge[s] = true
		return true
	}
	var SubsequencesGenerator func(idx int, s []byte, current []byte, knowledge map[int]string, bitmask []byte, pknowledge map[string]bool)
	SubsequencesGenerator = func(start int, s []byte, current []byte, knowledge map[int]string, bitmask []byte, pknowledge map[string]bool) {
		if start > len(s) {
			return
		}

		if len(current) > 0 {
			digit, _ := strconv.ParseInt(string(bitmask), 2, 32)
			if _, ok := knowledge[int(digit)]; !ok && IsPelindrom(string(current), pknowledge) {
				knowledge[int(digit)] = string(current)
			}

		}
		for i := start; i < len(s); i++ {
			bitmask[i] = '1'
			current := append(current, s[i])
			if i == start {
				SubsequencesGenerator(i+1, s, current, knowledge, bitmask, pknowledge)
			} else {
				s[i], s[start] = s[start], s[i]
				SubsequencesGenerator(i+1, s, current, knowledge, bitmask, pknowledge)
				s[i], s[start] = s[start], s[i]
			}
			bitmask[i] = '0'
