class Solution {
public:
    int IsPal(vector<int> &dp, string &s, int mask)
    {
        if(dp[mask]!=-1)return dp[mask];
        string x = "";
        for(int i=0;i<s.size();i++)
        {
            if((mask&(1<<i)) > 0)x += s[i];
        }
        int l=0, r = x.size()-1;
        while(l<r){
            if(x[l]!=x[r])return dp[mask] = 0;
            l++;r--;
        }
        return dp[mask] = 1;
    }
    int maxProduct(string s){
        int n = s.size(), ans = 1;
        vector<int> dp((1<<n), -1);
        for(int i=1;i<(1<<n);i++)
            for(int j=1;j<(1<<n);j++)
                if((i&j) == 0 && IsPal(dp,s,i) && IsPal(dp,s,j))
                    ans = max(ans, __builtin_popcount(i)*__builtin_popcount(j));
        return ans;
    }
};
