# Approach
Store all the subsequences of indices which will result into a palindrome. Then check for each pair of subsequence whether they are disjoint or not (the indices present in the pair of subsequences should be different). 


# Code
```
class Solution {
public:
vector<vector<int>>ans;
bool isPalindrome(vector<int>&p, string str) {
    sort(p.begin(), p.end());
    int len = p.size();
    for(int i=0; i<len/2; i++) {
        if(str[p[i]] != str[p[len-i-1]]) {
            return false;
        }
    }
    return true;
}
    void printSubSeqRec(string &str, int n, int index, vector<int>indices = {}){
        // base case
        if (index == n)
            return;

        if (!indices.empty()) {
            if(isPalindrome(indices, str)) {
                ans.push_back(indices);
            }
        }

        for (int i = index+1; i < n; i++) {
            indices.push_back(i);
            printSubSeqRec(str, n, i,  indices);
            indices.pop_back();
        }
        return;
    }
    bool isDisjoint(vector<int>&s, vector<int>&t) {
        int i=0;
        int j =0 ;
        int n= s.size();
        int m = t.size();
        while(i < n && j < m) {
            if(s[i] == t[j]) {
                return false;
            }
            if(s[i] < t[j]) {
                i++;
            }
            else {
                j++;
            }
        }
        return true;
    }
    int maxProduct(string s) {
       printSubSeqRec(s, s.size(), -1,{});
        // for(auto it: ans ) {
