        int ansi = 0;
        for(int i=0; i<len; i++) {
            for(int j= i+1; j < len; j++) {
                if(isDisjoint(ans[i], ans[j])) {
                    ansi = max(ansi, (int)ans[i].size()*(int)ans[j].size());
                }
            }
        }
