class Solution {
    func maxProduct(_ s: String) -> Int {
        var string = Array(s)
        var N = s.count
        var palindromHash = Dictionary<Int, Int>() //mask: lenght

        // leet code com0
        // 0000 0000 0001
        for mask in 1..<(1 << N) { // 1 -> 2 ^ N
            var paliCandidate = [Character]()
            for i in 0..<N {
                if mask & (1 << i) != 0 {
                    paliCandidate.append(string[i])
                }
            }

            if paliCandidate == paliCandidate.reversed() {
                palindromHash[mask] = paliCandidate.count
            }
        }

        var result = 0

        for (mask1, lenght1) in palindromHash {
            for (mask2, lenght2)  in palindromHash {
                if mask1 & mask2 == 0 {
                    result = max(result, lenght1 * lenght2)
                }
            }
        }

        return result
    }
}
