class Solution {
public:
    // string of size 12, make 2 decisions at each index
    // 2 ^ 12 = 4096
    // then when reach end, take 2 ^ 12 to find longest palidrome within string, = 4096
    // = 4096 * 4096 = 16777216
    // memoization is optional for a such a short string
	// if memoization becomes 4096 * 12 * 12
    int m(string& s, int l, int r){
        if (l >= r) return l == r;
        if (s[l] == s[r]) return 2 + m(s, l + 1, r - 1);
        return max(m(s, l + 1, r), m(s, l, r - 1));
    }
    int dfs(string& a, string& b, string& s, int i){
        if (i == s.size())
            return m(a, 0, a.size() - 1) * m(b, 0, b.size() - 1);
            int ans = dfs(a += s[i], b, s, i + 1);
            a.pop_back();
            ans = max(ans, dfs(a, b += s[i], s, i + 1));
            b.pop_back();
            return ans;
    }
    int maxProduct(string s) {
        string a = "", b = "";
        return dfs(a, b, s, 0);
    }
};
