package main

func isPalin(s string) bool {
	for i := 0; i < len(s)/2; i++ {
		if s[i] != s[len(s)-1-i] {
			return false
		}
	}
	return true
}

func maxProduct(s string) int {
	m := map[int]int{}
	p := map[string]bool{}
	var gen func(i int, t string, ti int) bool
	gen = func(i int, t string, ti int) bool {
		if i >= len(s) {
			return true
		}

		newT := t + s[i:i+1]
		newTi := ti | (1 << i)
		if p[newT] || isPalin(newT) {
			p[newT] = true
			m[newTi] = len(newT)
		}

		// choose i
		gen(i+1, newT, newTi)
		// not choose i
		gen(i+1, t, ti)

		return false
	}
	gen(0, "", 0)

	res := 0
	for k1, v1 := range m {
		for k2, v2 := range m {
			if k1&k2 == 0 && v1*v2 > res {
				res = v1 * v2
			}
		}
	}
	return res
}

