var maxProduct = function (s) {
    function isapalin(a) {
        let temp = [...a];
        let temps = temp.join("");
        let tempsrev = temp.reverse().join("");
        return temps === tempsrev;
    }

    let max = -Infinity;

    function bt(start = 0, ar1 = [], ar2 = []) {
        if (start === s.length) {
            if (isapalin(ar1) && isapalin(ar2)) {
                let m = ar1.length;
                let n = ar2.length;
                max = Math.max(max, m * n);
            }
            return;
        }

        ar1.push(s[start]);
        bt(start + 1, ar1, ar2);
        ar1.pop();

        ar2.push(s[start]);
        bt(start + 1, ar1, ar2);
        ar2.pop();

        bt(start + 1, ar1, ar2);
    }

    bt();
    return max;
};
```

