var maxProduct = function (s) {
    function isapalin(a, i = 0, j = a.length - 1) {
        for (; i <= j && a[i] === a[j]; i++, j--) {}
        return i > j;
    }
    function isbpalin(m, a) {
        let temp = [];
        for (let i = s.length - 1; m > 0; i--, m = m >> 1) {
            if (m & 1) {
                temp.push(a[i]);
            }
        }
        return [isapalin(temp), temp.length];
    }

    let max = -Infinity;
    let m1 = 0;
    let m2 = 0;

    function bt(start = 0) {
        if (start === s.length) {
            let [is1, m] = isbpalin(m1, s);
            if (is1) {
                let [is2, n] = isbpalin(m2, s);
                if (is2) {
                    max = Math.max(max, m * n);
                }
            }
            return;
        }

        m1 = m1 | (1 << start);
        bt(start + 1);
        m1 = m1 & ~(1 << start);

        m2 = m2 | (1 << start);
        bt(start + 1);
        m2 = m2 & ~(1 << start);

        bt(start + 1);
    }

    bt();
    return max;
};
