const maxProduct = (s) => {
    function ispalin(a, i = 0, j = a.length - 1) {
        for (; i <= j && a[i] === a[j]; i++, j--) {}
        return i > j;
    }
    const isdisjoint = (set1, set2) => {
        for (const e of set1)
            if (set2.has(e)) {
                return false;
            }
        return true;
    };

    let n = s.length;
    let aa = [];
    for (let ss = 0; ss < 1 << n; ss++) {
        let sseq_s = "",
            sseq_idx_set = new Set();
        for (let bm = 1, i = 0; bm <= ss; bm <<= 1, i++) {
            if (ss & bm) {
                sseq_s += s[i];
                sseq_idx_set.add(i);
            }
        }
        if (ispalin(sseq_s)) {
            aa.push([sseq_s.length, sseq_idx_set]);
        }
    }

    let m = aa.length;
    let max = 0;
    for (let i = 0; i < m; i++) {
        for (let j = i + 1; j < m; j++) {
            let [len1, sseq_idx_set1] = aa[i];
            let [len2, sseq_idx_set2] = aa[j];
            if (isdisjoint(sseq_idx_set1, sseq_idx_set2)) {
