        dfs(i + 1, str + s[i], pick | (1 << i));
        dfs(i + 1, str, pick);
    };
    dfs(0, "", 0);

    const a_ss = Object.keys(subPal).map((k) => +k);
    let max = 0;
    for (let i = 0; i < a_ss.length; ++i) {
        INNER: for (let j = 0; j < a_ss.length; ++j) {
            const ss1 = a_ss[i];
            const ss2 = a_ss[j];
            for (let k = 0; k < n; ++k) {
                if (
                    ss1 & (1 << k) && //
                    ss2 & (1 << k)
                )
                    continue INNER;
            }

            const subPal1 = subPal[ss1];
            const subPal2 = subPal[ss2];
            max = Math.max(max, subPal1.length * subPal2.length);
        }
    }
    return max;
