int maxPalindromic(char *str){
    if(strlen(str)==1) return 1;
    int len = strlen(str);
    int dp[len][len];

    dp[0][0] = 1;
    for(int i=1; i<len; ++i){
        dp[i][i] = 1;
        dp[i-1][i] = str[i]==str[i-1] ? 2 : 1;
    }

    for(int i=2; i<len; ++i){
        for(int j=0; i+j<len; ++j){
            int head = j, tail = i+j;
            dp[head][tail] = str[head]==str[tail] ? dp[head+1][tail-1]+2 : fmax(dp[head+1][tail], dp[head][tail-1]);
        }
    }

