class Solution {
public:
    int palindromeLen(string &str){
        int low=0;
        int high=str.size()-1;
        while(low<=high){
            if(str[low]!=str[high]){
                return 0;
            }
            low++;
            high--;
        }
        return str.size();
    }
    int generate_Subsequences(string &s, int curr, string str1, string str2){
        if(curr==s.size()){
            return palindromeLen(str1)*palindromeLen(str2);
        }

        str1+=s[curr];
        int ans1=generate_Subsequences(s,curr+1,str1,str2);
        str1.pop_back();

        str2+=s[curr];
        int ans2=generate_Subsequences(s,curr+1,str1,str2);
        str2.pop_back();

        int ans3=generate_Subsequences(s,curr+1,str1,str2);  // IGNORE

        return max(ans1,max(ans2,ans3));
    }
    int maxProduct(string s) {
        return generate_Subsequences(s,0,"","");
    }
}; // O(3^n)
```

