Here we first generate all subsequences and their masks which are palindromic. This will take O(2^n) time. Now we iterate over these masks and two masks are disjoint if their AND (&) is 0. Then we can use STL popcount, to get length and take maximum

```
class Solution {
public:
    vector<int> palindromicMask;  // palindromic mask

    bool isPalin(string &build){
        int i=0;
        int j=build.size()-1;
        while(i<j){
            if(build[i]!=build[j]){
                return false;
            }
            i++;
            j--;
        }
        return true;
    }

    void generate(string &s, int curr, string build, int mask){
        if(curr==s.size()){
            if(build!="" && isPalin(build)==true){
                palindromicMask.push_back(mask);
            }
            return;
        }
        generate(s,curr+1,build+s[curr],mask | (1<<curr));
        generate(s,curr+1,build,mask);
    }

    int maxProduct(string s) {
        generate(s,0,"",0);

        int res=0;

        for(int i=0;i<palindromicMask.size();i++){
            for(int j=i+1;j<palindromicMask.size();j++){
                if((palindromicMask[i] & palindromicMask[j])==0){  // NON OVERLAPPING
                    int l1=__builtin_popcount(palindromicMask[i]);
                    int l2=__builtin_popcount(palindromicMask[j]);
                    res=max(res,l1*l2);
                }
            }
        }
        return res;
    }
}; 
