class Solution {
      public int maxProduct(String s) {
         return maxProdPalind(s, 0, "", "");
    }

    boolean checkPalin4(String str) {
        for(int i=0; i<str.length()/2; i++) {
            int len = str.length()-1-i;
            if(str.charAt(i) != str.charAt(len)) {
                return false;
            }
        }
        return true;
    }

    int maxProdPalind(String s, int start, String first, String second) {
         if(start >= s.length()) {
             return (!first.isEmpty() && checkPalin4(first) ? first.length() : 0) * (!second.isEmpty() && checkPalin4(second) ? second.length() : 0);
         }
         int val2  = maxProdPalind(s, start+1, first.concat(String.valueOf(s.charAt(start))), second);
         int val4  =  maxProdPalind(s, start+1, first, second.concat(String.valueOf(s.charAt(start))));
         int val3 = maxProdPalind(s, start+1, first, second);
         return Math.max(val2, Math.max(val3, val4));
    }
}
