class Solution:
    def maxProduct(self, s: str) -> int:
        n = len(s)
        res = 0
        for mask in range(1, (1 << n) - 1):
            sub1, sub2 = [], []
            for i in range(n):
                if mask & 1:
                    sub1.append(i)
                else:
                    sub2.append(i)
                mask >>= 1
            if self.isPal(s, sub1):
                res = max(res, len(sub1) * self.longestPalSub(s, sub2))
        return res

    def isPal(self, s, sub):
        return all(s[sub[i]] == s[sub[~i]] for i in range(len(sub) >> 1))

    def longestPalSub(self, s, sub):
        n = len(sub)
        dp = [0] * n
        for left in range(n - 1, -1, -1):
            prev = 1
            for right in range(left + 1, n):
                if s[sub[left]] == s[sub[right]]:
                    cur = 2 + dp[right - 1]
                else:
                    cur = max(dp[right], prev)
                dp[right - 1] = prev
                prev = cur
            dp[n - 1] = prev
        return dp[n - 1]

