class Solution {
public:

    // We take two paths and use backtracking.
    // At each level 
    // we append the current character 
    // 1.in path1 and recurse
    // 2.in path2 and recurse 
    // 3.do not append the current character

    // At the end of the string we check if both the paths are palindrome and check the maximum product
    string path1;
    string path2;

    int ans = -1e9;

    int max(int a, int b)
    {
        if(a>b)
        return a;
        return b;
    }
    bool check(string &str)
    {
        int i = 0, j = str.length()-1;
        while(i<j)
        {
            if(str[i++]!=str[j--])
            return false;
        }

        return true;
    }

    void rec(string &str, int level)
    {
        if(level==str.length())
        {
            if(check(path1)&&check(path2))
            ans = max(ans,path1.length()*path2.length());
            return;
        }

        path1+=str[level];
        rec(str,level+1);
        path1.pop_back();

        path2+=str[level];
        rec(str,level+1);
        path2.pop_back();

        rec(str,level+1);
    }

    int maxProduct(string s) {

            rec(s,0);

            return ans;

    }
};
