class Solution {
    private final int K = 2;

    int res = 1;
    List<StringBuilder> v = new ArrayList<>(K);
    public int maxProduct(String s) {
        for(int i = 0; i < K; i++)
            v.add(new StringBuilder());

        recur(s, 0);

        return res;
    }

    private void recur(String s, int start) {
        if(start == s.length()) {
            if(v.get(0).length() == 0 || v.get(1).length() == 0)
                return;
            if(isPalindrome(v.get(0)) && isPalindrome(v.get(1)))
                res = Math.max(res, v.get(0).length() * v.get(1).length());

            return;
        }

        for(int i = 0; i < K; i++) {
            v.get(i).append(s.charAt(start));
            recur(s, start + 1);
            v.get(i).deleteCharAt(v.get(i).length() - 1);
        }

        // Dont pick s.charAt(start) at all
        recur(s, start + 1);
    }

    private boolean isPalindrome(StringBuilder sb) {
        int i = 0, j = sb.length() - 1;

        while(i < j){
            if(sb.charAt(i++) != sb.charAt(j--))
                return false;
        }

        return true;
    }
}
