class Solution {
public:
    int ans = 0;

    int check(string &a)
    {
        string b = a;
        reverse(b.begin(),b.end());
        return b==a;
    }

    void solve(string &s,string &a,string &b,int i)
    {
        if(i==s.size())
        {
            if(check(a) and check(b))
            {
                int x = a.size();
                int y = b.size();
                ans = max(ans,x*y);
            }
            return;
        }
        solve(s,a,b,i+1);
        a.push_back(s[i]);
        solve(s,a,b,i+1);
        a.pop_back();

        b.push_back(s[i]);
        solve(s,a,b,i+1);
        b.pop_back();
    }
    int maxProduct(string s) {
        string a = "";
        string b = "";
        solve(s,a,b,0);
        return ans;
    }
};
