class Solution {
    int binPow(int num, int p, int m){
        long long int ans=1;
        long long int n=num;
        while (p){
            if (p&1) ans=((1LL*ans%m)*(n%m))%m;
            n = ((1LL*n%m)*(n%m))%m;
            p/=2;
        }
        return ans;
    }
    bool fermatPrime(int p){
        if (p<=1) return false;
        if (p==2 || p==3 || p==5 or p==7 or p==11) return true;
        for (int i=1;i<=5;i++){
            int a=2+rand()%(p-3);
            if (binPow(a,p-1,p)!=1) return false;
        }
        return true;
    }
    int oddPal(int num){
        int n=num;
        n/=10;
        while (n){
            int rem=n%10;
            num=num*10+rem;
            n/=10;
        }
        return num;
    }
public:
    int primePalindrome(int N) {
        set<int> tab;
        for (int i=1;i<=2*1e4+7;i++){
            if (i==11){
                tab.insert(11);
                continue;
            }
            int p=oddPal(i);
            if (fermatPrime(p)) tab.insert(p);
        }
        auto idx=tab.lower_bound(N);
        return *idx;
    }
};
