class Solution {
public:
    char notprime[15000]={0};
    vector<int> primes;
    // pow10(i) == 10^i
    int pow10[9];

    Solution(){
        primes.push_back(2);
        for (int i=3;i < 15000;i+=2){
            if (notprime[i]) continue;
            primes.push_back(i);
            for (int j=i+i; j < 15000; j+=i)
                notprime[j] = 1;
        }

        for (int i=0, d=1;i<9;i++, d*=10){
            pow10[i]=d;
        }
    }

    int revdigits(int a, int ndigits){
        int b = 0;
        while (ndigits--){
            b = b*10 + a%10;
            a /= 10;
        }
        return b;
    }

    bool isprime(int a){
        int limit = ceil(sqrt(a));
        for (int i=0 ; i < primes.size() && primes[i] <=limit ;i++)
            if (a%primes[i] == 0)
                return false;
        return true;
    }

    inline int numdigits(int a){
        return floor(1+ log10(a));
    }

    // ndigits: number of digits in "a"
    inline int makepalin(int a,int ndigits, bool even){
         if (even){
             return a * pow10[ndigits] + revdigits(a,ndigits);
         }
         return a * pow10[ndigits-1] + revdigits(a/10,ndigits-1);
    }

    int primePalindrome(int N) {
        if (N <= 2) return 2;

        for (int nd = numdigits(N); ; nd ++){
            int first = pow10[(nd-1)/2];
            int last = pow10[(nd-1)/2+1] ;
            for (int i= nd==numdigits(N)? N / pow10[nd/2] : first; i < last;i++){
                // check whether start with even digit, if so, 
                // it will also end with even digit, hence skip to the next batch 
                if ((i/first) % 2 == 0) {
                    i += first-1;
                    continue;
                }
                // make palindrome of length "nd"
                int palin=makepalin(i, (nd+1)/2, nd%2==0);
                if (palin >= N && isprime(palin)) return palin;
            }
        }

        return 0;
    }
};
