	public int primePalindrome(int N) {
		if (N == 1) return 2;
		while (true) {
			if (checkPalindrome(N) && checkPrime(N)) {
				return N;
			}
			N++;
			if (10_000_000 < N && N < 100_000_000)
				N = 100_000_000;
		}
	}

	public boolean checkPalindrome(int N) {
		char[] arr = Integer.toString(N).toCharArray();
		int len = arr.length;
		int i;
		int j;
		int mid = len / 2;
		if (len % 2 == 0) {
			i = mid - 1;
			j = mid;
		}
		else {
			i = mid - 1;
			j = mid + 1;
		}
		while (i < j && i >= 0) {
			if (arr[i--] != arr[j++]) {
				return false;
			}
		}
		return true;
	}

