from math import trunc
class Solution:
    def primePalindrome(self, k: int) -> int:
	"""
	Find the closest root from the number and start from there rather than start from the first for all numbers
	"""
        if k < 12:
            for i in range(1, 12):
                if self.__is_prime(i) and i >= k:
                    return i
        else:
            string_k = str(k)
            string_length = len(string_k)
            if string_length % 2 == 0:
                starting_root = str(10**(string_length-trunc(string_length/2)))
                for root in range(int(starting_root), 10**6):
                    palindrome_gen = int(str(root) + str(starting_root)[-2::-1])
                    if self.__is_prime(palindrome_gen) and palindrome_gen >= k:
                        return palindrome_gen
            else:
                starting_root = string_k[:string_length-trunc(string_length/2)]
                for root in range(int(starting_root), 10**6):
                    palindrome_gen = int(str(root) + str(root)[-2::-1])
                    if self.__is_prime(palindrome_gen) and palindrome_gen >= k:
                        return palindrome_gen


    def __is_prime(self, num):
        if num == 1:
            return False
        for i in range(2, int(num**0.5)+1):
            if num % i == 0:
                return False
        return True
