class Solution {
public:
    bool isPal(int x) {
        int reverse = 0, src = x;
        while (x) {
            reverse = reverse * 10 + x % 10;
            x /= 10;
        }
        return reverse == src;
    }

    int nextPal(int x) {
        int n = 0, m, tmp;
        while(pow(10, n) <= x) n ++;
        for (m = (n - 1) / 2; m >= 0; m --) {
            tmp = x / (int)pow(10, m) % 10;
            if (tmp < 9) {
                if (n % 2 && m == (n-1)/2) return x + pow(10, m);
                return x + pow(10, m+1) + pow(10, m);
            }
        }
        return x + 2;
    }

    bool isPrime(int x) {
        if (x < 2) return false;
        int s = sqrt(x);
        for (int i = 2; i <= s; i ++) {
            if (x % i == 0) return false;
        }
        return true;
    }

    int primePalindrome(int N) {
        while (!isPal(N)) N ++;
        while(!isPrime(N)) N = nextPal(N);
        return N;
    }
};
