I find the next Palindrome code in https://stackoverflow.com/questions/7934519/a-better-algorithm-to-find-the-next-palindrome-of-a-number-string

```
import java.math.BigInteger;

class Solution {
    public int primePalindrome(int N) {
        if(N==1){
            return 2;
        }

        while(!isPalin(N)){
            N++;
        }

        if(isPrime(N)){
            return N;
        }

        while(true){
            N = Integer.parseInt(nextPalindrome(Integer.toString(N)));
            if(isPrime(N)){
                return N;
            }
        }

    }

    public String nextPalindrome(String num) {
        int len = num.length();
        String left = num.substring(0, len / 2);
        String middle = num.substring(len / 2, len - len / 2);
        String right = num.substring(len - len / 2);

        if (right.compareTo(reverse(left)) < 0)
            return left + middle + reverse(left);

        String next = new BigInteger(left + middle).add(BigInteger.ONE).toString();
        return next.substring(0, left.length() + middle.length())
             + reverse(next).substring(middle.length());
    }

    private String reverse(String s) {
        return new StringBuilder(s).reverse().toString();
    }

    private boolean isPalin(int num){
        if(num<10) return true;
        String str = Integer.toString(num);
        int i = 0;
        int j = str.length()-1;
        while(i<j){
            if(str.charAt(i)!=str.charAt(j)){
                return false;
            }
            i++;
            j--;
        }
        return true;
    }

    private boolean isPrime(int num){
        if(num==1) return false;
        if(num==2 || num==3 || num==5 || num==7) return true;
        for(int i = 2;i<(int)Math.sqrt(num)+1;i++){
            if(num%i==0){
                return false;
            }
        }
        return true;
    }

}
