class Solution {
  inline int generatePalindrome(int x) const {
    string str = to_string(x);
    string revStr = string(str.rbegin(), str.rend());
    return stoi(str + revStr.substr(1));
  }

  inline bool isPrime(int x) const {
    if (x < 2) return false;
    for (int i = 2; i*i <= x; i++)
      if (x % i == 0) 
        return false;
    return true;
  } 

  inline int ndigits(int x) const {
    int digits = 1;
    while (x /= 10)
      digits++;
    return digits;
  }

  inline int powi(int x, int k) const {
    if (k == 0) return 1;
    if (k == 1)
      return x;
    int res = powi(x, k / 2);
    res *= res;
    if (k & 1)
      res *= x;
    return res;
  }
public:    
    int primePalindrome(int n) {
	 // only palindrome with even length that is prime is 11
      if (n >= 8 && n <= 11) 
        return 11;

      // Find a good starting point so we aren't checking palindromes that are obviously too small
      int digits = ndigits(n), start;
      if (digits & 1) {
        start = n / powi(10, digits / 2);
      } else {
        // For example: n = 3044 -> the first possible palindrome that has 5 digits to check is 10001
        start = powi(10, digits / 2);
      }

      for (int i = start; i < 1e5; ++i) {
        int palindrome = generatePalindrome(i);
        if (palindrome >= n && isPrime(palindrome)) 
          return palindrome;
      }

      return 0;
    }
};

