from math import sqrt
class Solution:
    def isPrime(self, num):
        if num < 2 or num % 2 == 0:
            return num == 2
        for i in range(3, int(sqrt(num)) + 1, 2):
            if num % i == 0:
                return False
        return True
    def primePalindrome(self, n: int) -> int:
        if 8 <= n <= 11:
            return 11
        if len(str(n)) % 2 == 0:
            l = pow(10, len(str(n))//2)
        else:
            n_string = str(n)
            l = n_string[:len(str(n)) // 2 + 1]
        for i in range(int(l), 20000):
            tr = int(str(i)+ str(i)[:-1][::-1])
            if tr >= n and self.isPrime(tr):
                return tr
