class Solution(object):
    def isPrime(self, num):
        if num <= 1:
            return False
        if num <= 3:
            return True

        if num % 2 == 0 or num % 3 == 0:
            return False

        i = 5
        while i*i <= num:
            if num % i == 0 or num % (i + 2) == 0:
                return False
            i += 6

        return True

    def generate_special_palindrome(self, number):
        num_str = str(number)
        leng = len(num_str)
        if leng % 2 == 0:
            middle_digit = "-1"
        else:
            middle_digit = num_str[leng // 2]

        first_half = num_str[:leng // 2]
        if middle_digit != "-1":
            palindrome = int(first_half + middle_digit +
            first_half[::-1])
        else:
            palindrome = int(first_half +
            first_half[::-1])

        return palindrome

    def nextPal(self, number):
        num_str = str(number)
        leng = len(num_str)
        if leng % 2 == 0:
            middle_digit = "0"
            first_half = "1" + "0" * (leng/2 - 1)
        else:
            middle_digit = str(int(num_str[leng // 2]) + 1)
            first_half = num_str[:leng // 2]

        if middle_digit == "10":
            middle_digit = "0"
            first_half = str(int(first_half) + 1)
            palindrome = int(first_half + middle_digit +
            first_half[::-1])

        palindrome = int(first_half + middle_digit +
        first_half[::-1])

        return palindrome

    def primePalindrome(self, n):
        """
        :type n: int
        :rtype: int
        """
        z = self.generate_special_palindrome(n)
        if len(str(z)) == 1 or z == 10:
            while self.isPrime(z) == False:
                z += 1
            return z
        if z < n:
            z = self.nextPal(z)

        while self.isPrime(z) == False:
            z = self.nextPal(z)

        return z
