class Solution {
    public int primePalindrome(int n) {
        if (n<=11) {
            if (n<=2) return 2;
            if (n<=3) return 3;
            if (n<=5) return 5;
            if (n<=7) return 7;
            return 11;
        }

        String s = "" + n; 
        int guess = 1;
        while (true){
            for (char mid = '0' ; mid<='9'; mid++){
                StringBuilder pal = (new StringBuilder("" + guess)).append(mid).append((new StringBuilder("" + guess)).reverse().toString());
                int cur = Integer.parseInt(pal.toString());
