class Solution {
public:
    int primePalindrome(int n) {
        if(n==1){
            return 2;
        }
    int i = n - 1;
    while (true) {
        i++;
        int temp = i;
        int rev = 0;
        while (temp > 0) {
            rev = rev * 10 + temp % 10;
            temp = temp / 10;
        }
        if (rev == i) {
            int k = 2;
            int x = sqrt(i);
            while (k <= x) {
                if (i % k == 0) {
                    break;
                }
                k++;
            }
            if (k > x) {
                return i;
            }
        }
    }
    return -1;
}

};
