                n = 10001 ;
            }
            if( n > 99999 && n < 1000000 )
            {
                n = 1000001 ;
            }
            if( n > 9999999 && n < 100000000 )
            {
                n = 100000001 ;
            }

            else if(pal(n)&&prime(n))
                break;

            else
                n++;

        }

       return n;

    }

    public static boolean prime(int N)
    {

        int i ;

        if( ( N & 1 ) == 0 && N != 2 )
        {
            return false ;
        }

        else if( N % 3 == 0 && N != 3 )
        {
            return false ;
        }

        else if( N % 11 == 0 && N != 11 )
        {
            return false ;
        }

        else if( N % 13 == 0 && N != 13 )
        {
            return false ;
        }

        else if( N % 17 == 0 && N != 17 )
        {
            return false ;
        }

        else
        {
            for( i = 3 ; i <= Math.sqrt(N) ; i += 2 )
            {
                if( N % i == 0 )
                {
                    return false ;
                }
            }
        }

        return true ;

    }
    public static boolean pal(int u)
    {

        int z=Integer.toString(u).length()-1;
        int w=0;
        int ans=u;

        while(u>0)
        {

            int k=u%10;
            w=w+k*(int)Math.pow(10,z);
            z--;
            u=u/10;

        }

        if(w==ans)
            return true;

        else
            return false;

    }

}
