            result = 0;
            int i = 1;
            do
            {
                result = CreatePalindrome(i++, 10, j % 2);
                if (result >= n)
                {
                    if (j == 0)
                    {
                        max1stList = result;
                        flag1 = i - 1;
                    }
                    if (j == 1)
                    {
                        max2ndList = result;
                        flag2 = i - 1;
                    }
                    break;
                }
            } while (true);
        }

        for (int j = 0; j < 2; j++)
        {
            do
            {
                if (max1stList > max2ndList)
                {
                    int l = flag2;
                    while ((result = CreatePalindrome(l++, 10, 1)) < max1stList)
                    {
                        if (IsPrime(result)) return result;
                    }
                    if (IsPrime(max1stList)) return max1stList;
                }
                else if (max1stList < max2ndList)
                {
                    int l = flag1;
                    while ((result = CreatePalindrome(l++, 10, 0)) < max2ndList)
                    {
                        if (IsPrime(result)) return result;
                    }
                    if (IsPrime(max2ndList)) return max2ndList;
                }

                if (j == 0)
                {
                    result = CreatePalindrome(++flag1, 10, j % 2);
                    max1stList = result;
                }
                if (j == 1)
                {
                    result = CreatePalindrome(++flag2, 10, j % 2);
                    max2ndList = result;
                    j = -1;
                }
                break;
            } while (true);
        }
        return 0;
    }

    public bool IsPrime(int n)
    {
        if (n <= 1) return false;
        if (n <= 3) return true;

        // This is checked so that we can skip
        // middle five numbers in below loop
        if (n % 2 == 0 || n % 3 == 0) return false;

        for (int i = 5; i * i <= n; i = i + 6)
            if (n % i == 0 || n % (i + 2) == 0) return false;

        return true;
    }

    public int CreatePalindrome(int input, int b, int isOdd)
    {
        int n = input;
        int palin = input;

        if (isOdd == 1)
            n /= b;

        // Creates palindrome by just appending
        // reverse of number to itself
        while (n > 0)
        {
            palin = palin * b + (n % b);
            n /= b;
        }
        return palin;
    }
}
