            if (n % i == 0) {
                return false;
            }
        }
        return true;
    }
    public boolean isPalindrome(int n)
    {

        // Find reverse of n
        int rev = 0;
        for (int i = n; i > 0; i /= 10)
            rev = rev * 10 + i % 10;

        // If n and rev are same, 
        // then n is palindrome
        return(n == rev) ? true : false;
    }
}

