/**
 * @param {number} n
 * @return {number}
 */
var primePalindrome = function (n) {
  if (n < 3) return 2

  // iterator for next palindrome | 下一个对称数的迭代器
  let it = nextPalindrome(n)

  while (true) {
    let num = it.next().value // next palindrome | 下一个对称数
    if (isPrime(num)) return num // is prime and return | 如果是质数则返回
  }

  /**
   * is prime | 判断质数
   * @param {number} n 
   * @returns {boolean} 
   */
  function isPrime(n) {
    if (n % 2 === 0) return false
    for (let i = 3; i < n / 2; i += 2) {
      if (n % i === 0) return false
    }
    return true
  }

  /**
   * next palindrome | 下一个对称数
   * @param {number} n
   */
  function* nextPalindrome(n) {
    // 找到一半长度的数字，371=>37, 3772=>7, find first half number 
    let length = ('' + n).length 
    let halfLength = Math.floor(length / 2) 
    let halfNum = Math.floor(n / Math.pow(10, halfLength))

    // 可能n就是对称的 | case n is palindrome
    let first = half2full(halfNum, halfLength)
    if (first >= n) yield first

    // 更大的对称数 | larger palindromes
    while (true) yield nextNum()

    /**
     * 更大的对称数 | larger palindromes
     * @returns {number}
     */
    function nextNum() {
      // 37(3)=>38(3)，37(73)=>38(83)
      halfNum++

      // 9=>11 (9=>10(/10)=>1=>1(1))
      // 99=>1001 (9=>10=>10(01))
      if (Math.log10(halfNum) % 1 === 0) {
        if (length % 2) halfNum /= 10
        length++
        halfLength = Math.floor(length / 2)
      }

      // 37=>373 | 37=>3773  
      return half2full(halfNum, halfLength)
    }

    function half2full(halfNum, halfLength) {
      return halfNum * Math.pow(10, halfLength) + getReversed(halfNum, halfLength)

      function getReversed(num, length) {
        let arr = []
        while (num) {
          let v = num % 10
          num -= v
          num /= 10
          arr.push(v)
        }
        let rtn = 0
        for (let i = 0; i < length; i++) {
          rtn *= 10
          rtn += arr[arr.length - length + i]
        }
        return rtn
      }
    }
  }
};

