void gen(string s,int i,bool flag,string t=""){

     if(i == s.size()){
           int i = 0;
           while(i<s.size() && t[i]=='0')
                  i++;

           t =string(t.begin()+i,t.end());
           string tr= t;
           reverse(tr.begin(),tr.end());

            if(isPrime(atoi((t + tr).c_str()))){
                  V.push_back(atoi((t + tr).c_str()));
