class Solution:
    def primePalindrome(self, N: int) -> int:
        def isPrime(n):
            if n<=2 or n&1==0:
                return n==2
            for i in range(3, int(n**0.5)+1, 2):
                if n%i==0:return False
            return True

        def nextPalindrome(n):
            s = str(n)
            l = len(s)
            if n < 10:
                return n+1
            # 123 12+1 ->131
			# if left half is smaller than right half, increase left help by one
            if s[:l//2]<=s[(l+1)//2:]:
                head = str(int(s[:(l+1)//2])+1)
            else:
                head = s[:(l+1)//2]
            ans = head+head[:l//2][::-1]
			# if the palindrom is the same, increase left halp by 1
            if ans==str(n):
                head = str(int(s[:(l+1)//2])+1)
                ans = head+head[:l//2][::-1]
            return int(ans)
		# in case N is already a result, we start with N-1
        n = N-1
        while True:
            p = nextPalindrome(n)
            if isPrime(p):
                return p
            else:
                n = p

