/**
 * @param {number} N
 * @return {number}
 */
var primePalindrome = function(N) {
    if (N <= 2) return 2
    if (N <= 3)  return 3
    if (N <= 5) return 5
    if (N <= 7) return 7


    const tails = [1, 3, 7, 9]
    function isPrime(num) {
        for (let modMe = 2, sq = Math.sqrt(num);
            modMe <= sq; modMe++) {
            if (num % modMe === 0)  return false
        }

        return true
    }


    for (let digitC = Math.max(String(N).length, 2); ; digitC++) {
        const halfC = digitC >> 1
        const isOdd = Boolean(digitC & 1)
        const anyC = -1 + halfC
        const valLimit = 10 ** anyC

        let midFrom = 0, midTo = 9
        if (!isOdd) {
            midTo = 0
        }

        for (const tail of tails.values()) {
            for (let val = 0; val < valLimit; val++) {
                let valStr = String(val)
                valStr = valStr.padStart(anyC, '0')
                const sub = [...valStr]
                const subReverse = [...sub].reverse()

                for (let mid = midFrom; mid <= midTo; mid++) {
                    const chs = [tail]

                    if (anyC !== 0) chs.push(...sub)
                    if (isOdd)  chs.push(mid)
                    if (anyC !== 0) chs.push(...subReverse)
                    chs.push(tail)

