class Solution {
    func primePalindrome(_ N: Int) -> Int {
        let str = String(N)
        var digits = str.count

        if isPrime(N) && isPalindrome(N) {
            return N
        }

        var found = -1
        while found == -1 {
            palindromes(digits, N, &found)
            digits += 1
        }
        return found
    }

    func isPalindrome(_ num: Int) -> Bool {
        let str = String(num)
        return str == String(str.reversed())
    }

    func isPrime(_ num: Int) -> Bool {
        if num == 1 { return false }
        var division = 2
        while true {
            if division * division > num {
                // we checked all candidates
                return true
            }

            if num % division == 0 {
                return false
            }
            division += 1
        }
        return false
    }

    func palindromes(_ digit: Int, _ N: Int, _ found: inout Int) -> [String] {
        let nums = (0...9).map { String($0) }
        if digit == 0 { return [""] }
        if digit == 1 {
            for i in 1...9 where i >= N && isPrime(i) {
                found = i
                break
            }
            return nums
        }

        var results = [String]()
        let prev = palindromes(digit-2, N, &found)

        for num in nums {
            for val in prev {
                let str = num+val+num
                results.append(str)

                if num != "0", let int_val = Int(str), int_val >= N, isPrime(int_val) {
                    found = int_val
                    return results
                }
            }
        }
        return results
    }
}
